import axios from 'axios'

const getUsersData = ({ accessToken, tokenType, baseUrl }) => {
    return new Promise((resolve, reject) => {
        axios({
            method: 'POST',
            url: baseUrl + "/odata/v2/EmpJob?$select=seqNumber,startDate,userId,userNav/defaultFullName,jobTitle,managerId,managerUserNav/defaultFullName&$expand=userNav,managerUserNav&$format=json", // https://cors-anywhere.herokuapp.com/
            headers: {
                "Authorization": tokenType + " " + accessToken
            },
            data: params,
        }).then((response) => {
            console.log(response);
            resolve(response)
        })
    })
}

const getAccessToken = (payload, assertion) => {
    return new Promise((resolve, reject) => {
        const params = new URLSearchParams();
        params.append('company_id', payload.companyId);
        params.append('client_id', payload.clientId);
        params.append('grant_type', payload.grantType);
        params.append('user_id', payload.userId);
        params.append('assertion', assertion);
        axios({
            method: 'POST',
            url: payload.API_BASE_URL + '/oauth/token', // https://cors-anywhere.herokuapp.com/
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            data: params,
        }).then((response) => {
            resolve(response)
            console.log("Get Access Token Response", response)
        })
    })
}

const authentiateUser = (payload) => {
    return new Promise((resolve, reject) => {
        const params = new URLSearchParams();
        params.append('client_id', payload.clientId);
        params.append('user_id', payload.userId);
        params.append('token_url', payload.tokenUrl);
        params.append('private_key', payload.privateKey);
        axios({
            method: 'POST',
            url: payload.API_BASE_URL + '/oauth/idp',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            data: params,
        }).then((response) => {
            console.log(response);
            resolve({ payload: payload, data: response.data })
        }).catch((error) => {
            console.log(error)
        })
    })

}

module.exports = () => {
    const authenticateAndGetData = (payload) => {
        return new Promise((resolve, reject) => {
            authentiateUser(payload).then((authResponse) => {
                getAccessToken(payload, authResponse).then((tokenResponse) => {
                    getUsersData({ accessToken: tokenResponse.data.access_token, tokenType: tokenResponse.data.token_type, baseUrl: payload.API_BASE_URL }).then((data) => {
                        console.log("Final Data" + data)
                        resolve(data)
                    })
                })
            })

        })
    }
    return {
        authenticateAndGetData
    }
}